import styled from 'styled-components'

export const Container = styled.div`
  width: 100%;
  max-width: 550px;
  background-color: red;
  padding: 16px;
  border-radius: 10px;
  background-color: #C0C0C0;
`

export const ListCard =  styled.div`
background-color: red;
display: flex;
align-items: center;
`

export const ImageProduct = styled.div`
width: 130px;
height: 130px;
background-color: yellow;
margin: 0 20px;
border-radius: 50%;`

export const InfosProducts = styled.div`
display: flex;
flex-direction: column;`

export const BonusInfos = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: green;
  padding: 5px;


  h1 {
    font-size: 16px;
    color: #04183E;
    font-weight: bold;
    margin-right: 15px;
  }

  span {
    font-size: 16px;
    color: #04183E;
    margin-right: 15px;
  }
`
